using VestLivros.Models;
using Microsoft.AspNetCore.Identity;
using Microsoft.EntityFrameworkCore;

namespace VestLivros.Data;

public class AppDbSeed
{
    public AppDbSeed(ModelBuilder builder){
        List<Faculdade> faculdades = new() {
            new Faculdade { Id = 1, FaculdadeNome = "Unicamp"},
            new Faculdade { Id = 2, FaculdadeNome = "USP"},
        };
        builder.Entity<Faculdade>().HasData(faculdades);

        List<Vestibular> vestibulares = new() {
            new Vestibular { Id = 1, Ano = "2026"},
            new Vestibular { Id = 2, Ano = "2027"},
            new Vestibular { Id = 3, Ano = "2028"},
            new Vestibular { Id = 4, Ano = "2029"},
        };
        builder.Entity<Vestibular>().HasData(vestibulares);

        List<Livro> livros = new List<Livro> {
            // Livros de 2026 - somente haverá informações do que foi colocado na Model //
            new Livro { Id = 1, FaculdadeId = 2, Nome = "As meninas", PDF = "", Resumo = "Num pensionato de freiras paulistano, em 1973, três jovens universitárias começam sua vida adulta de maneiras bem diversas. A burguesa Lorena, filha de família quatrocentona, nutre veleidades artísticas e literárias. Namora um homem casado, mas permanece virgem. A drogada Ana Clara, linda como uma modelo, divide-se entre o noivo rico e o amante traficante. Lia, por fim, milita num grupo da esquerda armada e sofre pelo namorado preso. As meninas colhe essas três criaturas em pleno movimento, num momento de impasse em suas vidas. Transitando com notável desenvoltura da primeira pessoa narrativa para a terceira, assumindo ora o ponto de vista de uma ora de outra das protagonistas, Lygia Fagundes Telles constrói um romance pulsante e polifônico, que capta como poucos o espírito daquela época conturbada e de vertiginosas transformações, sobretudo comportamentais.", AnaliseCritica = "Lorem Ipsum", Contexto = "foi publicado em 1973 e se insere num período de intensas transformações políticas e sociais no Brasil – época marcada pelo regime militar, censura e repressão política, que impactaram profundamente a vivência e a expressão cultural da época.obra de profunda relevância literária e sociopolítica que se insere no contexto da ditadura militar no Brasil, representando, por meio da história de três jovens universitárias, a pluralidade de experiências femininas e os desafios impostos por um regime repressivo. Com uma narrativa inovadora que mescla múltiplas perspectivas e monólogos interiores, o romance rompe com as convenções tradicionais da prosa, criando um espaço onde o pessoal e o político se entrelaçam e onde a resistência se manifesta de forma sutil, mas poderosa.", ArquivoFoto = "/img/as meninas.jpg"},
            new Livro { Id = 2, FaculdadeId = 2, Nome = "Casa velha", PDF = "", Resumo = "A Casa Velha, de Machado de Assis, é um romance publicado em 1885 e ambientado no Rio de Janeiro do século XIX. A história foca nas relações familiares e sociais, acompanhando os conflitos de valores entre as gerações e as complexidades das relações humanas. No centro da trama está a casa da família Aguiar, uma residência que simboliza tradição e decadência. A narrativa aborda as tensões entre o velho e o novo, explorando temas como hipocrisia, desejo reprimido e poder. Personagens complexos revelam o caráter da sociedade brasileira da época, enquanto o autor utiliza uma crítica sutil ao comportamento humano. A obra traz reflexões sobre moralidade, memória e identidade, sendo uma das muitas contribuições de Machado de Assis à literatura brasileira.", AnaliseCritica = "Lorem Ipsum", Contexto = "Brasil imperial (século XIX), em meio à aristocracia carioca, com forte marca do patriarcalismo.  Uma das obras da fase de transição de Machado, já revelando sua crítica social e o refinamento psicológico que marcaria seu Realismo.", ArquivoFoto = "/img/a casa velha.jpg"},
            new Livro { Id = 3, FaculdadeId = 1, Nome = "A paixão segundo G.H", PDF = "", Resumo = "A Paixão Segundo GH de Clarice Lispector é um livro que narra a jornada existencial da protagonista, GH, uma mulher de classe alta que, ao limpar o quarto da empregada doméstica que pediu demissão, encontra um evento surpreendente: uma barata dentro de um armário. Essa descoberta aparentemente simples leva GH a confrontar questões profundas sobre a existência, a natureza humana e sua própria identidade. Ao longo da narrativa, GH mergulha em reflexões filosóficas e metafísicas, explorando sentimentos de desconstrução e reconstrução de si mesma. A barata torna-se um símbolo poderoso, representando o desconhecido, o grotesco e, paradoxalmente, a universalidade da vida. A obra desafia o leitor a refletir sobre os dilemas da consciência, o silêncio e o vazio, buscando um entendimento mais profundo da própria existência. Clarice Lispector utiliza uma linguagem poética e introspectiva para criar uma narrativa imersiva e rica em nuances. O livro é considerado uma das obras-primas da literatura brasileira e continua fascinando leitores por sua profundidade e intensidade emocional.", AnaliseCritica = "Publicado em 1964, A Paixão Segundo G.H. é considerado um dos romances mais intensos e enigmáticos de Clarice Lispector. Construído como um profundo monólogo interior, conduzido pela própria protagonista, a narrativa acompanha uma mulher de classe alta — identificada apenas pelas iniciais G.H. — em uma experiência existencial e psicológica extrema. O texto segue o fluxo de consciência da personagem, sem enredo linear, revelando fragmentos de pensamentos, sensações e epifanias. Um recurso marcante é o fato de cada capítulo começar com a frase final do anterior, criando uma continuidade simbólica e reforçando a ideia de espiral de reflexões. A história parte de um acontecimento aparentemente banal: G.H. decide limpar o quarto da empregada Janair, que acabara de se demitir. Surpreende-se ao encontrar o espaço limpo e organizado, marcado por um desenho primitivo na parede — um homem, uma mulher e um cachorro — que desperta reflexões sobre hierarquia social e invisibilidade da trabalhadora doméstica. O ponto de ruptura ocorre quando G.H. abre o armário e se depara com uma barata. O choque inicial dá lugar a uma inquietante transformação interior. Ao matar o inseto e, num momento extremo, provar a substância branca que dele escapa, G.H. entra em contato com algo que percebe como essência da vida, um estado primário e universal, próximo do Nada. Essa experiência, ao mesmo tempo mística e perturbadora, desconstrói sua identidade e abala todas as certezas que possuía. O romance desenvolve temas centrais como a dissolução do eu, o confronto com o abjeto, a linguagem como via de aproximação ao indizível e a identificação com aquilo que é tradicionalmente rejeitado. A barata, nesse sentido, é símbolo do que a humanidade tenta excluir, mas que, ao ser encarado de frente, revela a precariedade da separação entre humano e não-humano. A relação com Janair também traz à tona um subtexto de conflito de classes e racismo estrutural: a empregada, mulher negra, é quase apagada da memória da patroa, e seu espaço pessoal se torna o gatilho para uma profunda ruptura existencial. Estilisticamente, Clarice Lispector articula uma prosa poética, filosófica e fragmentada, que exige do leitor mais do que compreensão racional — é preciso vivenciar a experiência. O livro dialoga com o existencialismo e com a tradição da narrativa introspectiva de autores como Proust, Joyce e Woolf. Por sua densidade, provoca reações diversas: alguns leitores veem na obra um mergulho transformador, enquanto outros a consideram de difícil compreensão. Clarice afirmou que este foi o livro que melhor expressou suas exigências como escritora. A Paixão Segundo G.H. transcende a ficção convencional ao fazer de um episódio doméstico a porta para um questionamento radical da existência. É, ao mesmo tempo, uma experiência literária, filosófica e sensorial, na qual o humano se confronta com o essencial e retorna — ou não — transformado", Contexto = " Publicado em 1964, ano do golpe militar no Brasil. O país entrava em uma ditadura que duraria 21 anos. Apesar de o livro não ser diretamente político, está imerso num momento de crise de valores, identidade e liberdade. Uma das obras mais profundas de Clarice. Usa o individual para tocar o universal; mostra a crise do sujeito moderno diante da solidão, da matéria e da consciência.", ArquivoFoto = "/img/a paixao segundo GH.jpg"},
            new Livro { Id = 4, FaculdadeId = 2, Nome = "A vida não é útil", PDF = "", Resumo = "A Vida Não É Útil, do líder indígena Ailton Krenak, é uma coletânea de ensaios que reflete sobre a crise ambiental, cultural e espiritual da humanidade. Publicada em 2020, a obra é um convite para repensarmos nosso estilo de vida e nossa relação com a Terra. Krenak critica a ideia de progresso e a busca incessante por crescimento, que desconsidera os limites da natureza e desumaniza as pessoas. Ele propõe uma visão mais conectada e harmoniosa, inspirada na cosmovisão indígena, onde a vida é valiosa por si mesma, sem a necessidade de utilitarismo. O livro é um chamado à consciência e à reflexão, incentivando a valorização da diversidade e a construção de um futuro mais sustentável. ", AnaliseCritica = "Lorem Ipsum", Contexto = "Brasil contemporâneo, em meio a crises ambientais, sociais e políticas. Obra-manifesto do pensador indígena brasileiro, que propõe repensar a vida sob a ótica da coletividade, da natureza e da ancestralidade", ArquivoFoto = "/img/a vida nn é util.jpg"},
            new Livro { Id = 5, FaculdadeId = 1, Nome = "A visão das plantas", PDF = "", Resumo = "O livro A Visão das Plantas, de Djaimilia Pereira de Almeida, é uma obra intensa e reflexiva que explora temas como a natureza humana, a memória e os limites da percepção. A narrativa gira em torno de um personagem chamado Amador, um ex-comandante de um navio negreiro que vive seus últimos dias cuidando de um jardim em um lugar remoto. Através da perspectiva de Amador e das plantas que ele cultiva, o livro nos leva a questionar o que significa existir e testemunhar a história. É uma história profunda sobre culpa, remorso e a tentativa de buscar redenção em um mundo cheio de cicatrizes.", AnaliseCritica = "Publicado em 2019, A Visão das Plantas é um romance curto, mas profundamente filosófico e poético, escrito por Djaimilia Pereira de Almeida, autora nascida em Luanda em 1982 e radicada entre Angola e Portugal. Doutora em Teoria da Literatura, Djaimilia constrói uma obra marcada pela reflexão sobre identidade, memória e herança colonial, inserida no contexto da literatura contemporânea pós-colonial. Entre seus reconhecimentos, está o segundo lugar no Prêmio Oceanos de 2020 com este livro, além de outros prêmios obtidos por obras anteriores. A narrativa acompanha o capitão Celestino, um ex-pirata e traficante de escravizados, já na velhice e recolhido à casa de infância em Portugal. Assombrado pelo passado brutal, ele dedica-se ao cuidado de um jardim, cultivando plantas como se buscasse redenção e reconciliação. O texto se desenvolve de forma não linear, alternando memórias, silêncios e reflexões, numa prosa introspectiva que dissolve fronteiras entre presente e passado. Os temas centrais da obra incluem a culpa, o remorso e a busca por redenção; a natureza como testemunha silenciosa e indiferente à moral humana; a ambiguidade ética, que s2ende o julgamento do leitor e convida a penetrar na complexidade de um homem que foi tanto monstro quanto cuidador; o silêncio e o esquecimento, que moldam a memória individual e coletiva; e a consciência da finitude, marcada pelo envelhecimento e pela percepção do tempo que se esvai. O jardim, nesse contexto, é metáfora de vida e renovação, mas também de silêncio cúmplice, capaz de acolher sem revelar. A prosa de Djaimilia é lírica, fragmentada e contemplativa, exigindo que o leitor sinta antes de compreender. A escolha de narrar de forma contida e poética um passado marcado por violência e injustiça cria uma tensão estética poderosa, que desafia certezas e provoca reflexão. A autora não oferece respostas ou absolvição, mas um espaço de s2ensão onde a beleza e a brutalidade coexistem. Assim, A Visão das Plantas se afirma como uma obra de grande densidade moral e literária, que confronta o leitor com a ambiguidade do ser humano e com a possibilidade — ou impossibilidade — de reconciliar-se com o passado. Ao colocar a natureza como observadora imperturbável, Djaimilia evidencia a distância entre o humano e o eterno, convidando-nos a refletir sobre o peso da memória e a fragilidade da redenção.", Contexto = "Embora a autora seja angolana-portuguesa, essa obra dialoga com o passado colonial português na África.  A narrativa desconstrói a figura de um antigo senhor de escravos por meio do olhar de quem ele foi, e não do que construiu – uma crítica sutil à romantização da história colonial. Questiona o legado da escravidão e da violência nas ex-colônias.", ArquivoFoto = "/img/a visao das plantas.jpg"},
            new Livro { Id = 6, FaculdadeId = 2, Nome = "Alice no país das maravilhas", PDF = "", Resumo = "Uma menina, um coelho e uma história capazes de fazer qualquer um de nós voltar a sonhar. Alice é despertada de um leve sono ao pé de uma árvore por um coelho peculiar. Uma criatura alva e falante com roupas engraçadas, que consulta seu relógio e reclama do próprio atraso. Curiosa como toda criança, Alice segue o animal até cair em um buraco sem fim que mudou para sempre a literatura infantil. Mais de 150 anos depois, Alice no País das Maravilhas continua repleto de ensinamentos para aqueles que ousaram seguir o Coelho Branco até sua toca.", AnaliseCritica = "Lorem Ipsum", Contexto = "Inglaterra vitoriana (século XIX), rígida, moralista, patriarcal. Um clássico da literatura nonsense e infantil, que ironiza convenções sociais e questiona padrões lógicos da época.", ArquivoFoto = "/img/alice no pais das maravilhas.jpg"},
            new Livro { Id = 7, FaculdadeId = 1, Nome = "Balada de amor ao vento", PDF = "", Resumo = "Balada de Amor ao Vento, de Paulina Chiziane, é um romance cativante ambientado em Moçambique, que aborda questões culturais, sociais e políticas do país. A obra é narrada por Delfina, uma mulher forte e resiliente, que conta sua trajetória marcada por amores, dores, tradições e a busca por sua própria identidade.", AnaliseCritica = "Publicado em 1990, Balada de Amor ao Vento é o primeiro romance escrito por uma mulher em Moçambique, um marco histórico e literário que inaugura uma tradição narrativa feminista no país. Sua autora, Paulina Chiziane, nasceu em Maputo em 1955, cresceu em um ambiente de resistência anticolonial e teve contato com a língua portuguesa apenas na escola, já que em casa falava idiomas maternos. Atuante politicamente durante a luta pela independência, Chiziane construiu uma escrita híbrida, que mescla o português com a cadência e a oralidade africanas, resultando em uma prosa de forte marca poética.A narrativa acompanha Sarnau desde a juventude, quando vive uma paixão arrebatadora e não correspondida por Mwando, até os anos em que enfrenta experiências duras como casamentos frustrados, maternidade, pobreza e prostituição. A relação entre Sarnau e Mwando, marcada por encontros e abandonos, atravessa o romance e serve de eixo para refletir sobre as tensões sociais, culturais e religiosas do Moçambique pós-colonial.A obra adota o formato de “balada” em prosa poética, evocando a tradição oral moçambicana, com frases curtas, repetições, ritmo cadenciado e uma estrutura não linear. Flashbacks e fragmentos de memória intensificam o tom emocional, enquanto o lirismo e os recursos de repetição aproximam a narrativa de uma performance contada à beira da fogueira.Entre os principais temas, destaca-se a condição feminina em uma sociedade poligâmica e patriarcal, que consagra a dominação masculina e restringe a autonomia das mulheres. A trajetória de Sarnau, marcada por violência simbólica e material, espelha a luta por dignidade e independência pessoal. O romance também aborda os resquícios do colonialismo português, revelando como a herança colonial molda as relações de poder e a identidade cultural no período pós-independência. A história de Sarnau é, ainda, um percurso de autodescoberta: ao longo das provações, ela constrói uma consciência própria e se afirma como mulher resiliente, capaz de sobreviver e resistir. A linguagem simbólica é outro ponto forte da obra. Elementos como o vento e o encontro amoroso funcionam como metáforas universais, aproximando a experiência de Sarnau de dilemas femininos reconhecíveis em outras culturas e épocas. Essa dimensão simbólica amplia o alcance do romance, permitindo que ele dialogue para além de seu contexto local. Com a inclusão na lista de leituras obrigatórias da Fuvest, Balada de Amor ao Vento reafirma sua relevância como obra essencial para refletir sobre gênero, cultura e poder. Ao transformar a dor íntima em matéria literária e ato político, Paulina Chiziane não apenas dá voz às mulheres moçambicanas, mas também reposiciona a literatura africana no cenário contemporâneo, mostrando que a experiência feminina é central para compreender as dinâmicas históricas e sociais do continente. Trata-se de uma narrativa que une poesia e denúncia, memória e resistência, consolidando-se como um testemunho poderoso da condição feminina e da capacidade de transformação através da palavra.", Contexto = "Moçambique pós-colonial, em transição de um sistema tradicional para o moderno.  Forte crítica ao sistema patriarcal e à opressão da mulher dentro da sociedade moçambicana. Primeira mulher moçambicana a publicar um romance, traz à tona o feminismo africano e a complexidade da condição feminina na sociedade pós-independência.", ArquivoFoto = "/img/balada de amor ao vento.jpg"},
            new Livro { Id = 8, FaculdadeId = 1, Nome = "Caminho de pedras", PDF = "", Resumo = "Caminho de Pedras, de Rachel de Queiroz, é um livro que narra as dificuldades e os conflitos vividos por trabalhadores rurais e camponeses no Brasil. A obra aborda questões sociais como desigualdade, exploração e injustiça, mostrando as lutas e os sonhos dos personagens em busca de uma vida digna. Com sua prosa cativante e sensível, Rachel de Queiroz retrata a força e a resistência dos mais vulneráveis, convidando o leitor a refletir sobre a realidade das classes sociais menos favorecidas. É um livro intenso e comovente, que combina crítica social com a rica descrição do cotidiano brasileiro.", AnaliseCritica = "Publicado em 1937, Caminho de Pedras é considerado o romance mais politicamente engajado de Rachel de Queiroz. Inserido no contexto da turbulência política que antecedeu o Estado Novo brasileiro, a obra mergulha em temas como militância, opressão e luta pela liberdade. Narrado em terceira pessoa por uma voz onisciente, o romance acompanha os personagens com total conhecimento de seus pensamentos e motivações, revelando uma Fortaleza dos anos 1930 marcada por repressão política e intensas transformações sociais.O protagonista, Roberto, é um jornalista comunista enviado do Rio de Janeiro para reorganizar uma célula operária em Fortaleza. Ele representa o intelectual engajado, imerso em um movimento fragmentado e cheio de conflitos internos. No entanto, é Noemi quem se destaca como a verdadeira alma da narrativa. Mulher casada e mãe de Guri, ela trabalha retocando fotografias — um gesto simbólico de editar a realidade — e, ao perder o emprego após sua separação, confronta-se com os limites impostos pela moralidade e pela pertença social. Sua trajetória entre opressão, desejo, maternidade e lutas políticas delineia um arco de emancipação, mesmo marcado por perdas dolorosas, como a morte do filho e o exílio de Roberto.Entre os temas centrais da obra, destaca-se o engajamento político e a fragmentação da militância. Rachel de Queiroz expõe um Partido Comunista dividido e debilitado pela repressão, longe da utopia revolucionária, inserindo uma crítica realista à militância da época. Outro ponto relevante é a crítica ao patriarcado e à identidade feminina: Noemi encarna a luta das mulheres por autonomia, enfrentando tanto o jugo das expectativas sociais quanto o rígido moralismo vigente. A autora constrói um realismo social cru, com cenas de fome, trabalho excessivo e perseguição política, mas permeado por uma poesia discreta e simbólica.O estilo literário de Rachel de Queiroz é marcado por uma escrita enxuta e coloquial, especialmente eficaz na construção de diálogos dramáticos e realistas, que intensificam o impacto emocional da história. O simbolismo do “caminho de pedras” reflete a trajetória árdua da protagonista — uma vida adulta permeada de obstáculos, mas também de esperança e resistência. Recentemente incorporado à lista de leituras obrigatórias da Fuvest, o romance reafirma sua relevância acadêmica e literária.Caminho de Pedras é, portanto, uma narrativa poderosa que entrelaça amor, política e resistência em meio a um tempo de censura e desconfiança. Rachel de Queiroz reflete com coragem sobre as contradições da militância, o peso social das escolhas femininas e as fissuras da luta política. Sua escrita, direta e sem concessões, modela uma protagonista que, mesmo sofrendo perdas irreparáveis, segue adiante — carregando a vida e a luta consigo.", Contexto = "Brasil urbano do século XX, com foco nos dramas familiares e na hipocrisia da classe média. Nelson escancara as máscaras sociais e mostra o lado sombrio das relações humanas. Seu olhar cru sobre a sociedade chocava e, ao mesmo tempo, revelava tabus escondidos no cotidiano brasileiro.", ArquivoFoto = "/img/caminho de pedras.jpg"},
            new Livro { Id = 9, FaculdadeId = 1, Nome = "Canção para ninar menino grande", PDF = "", Resumo = "Canção de Ninar para Menino Grande, de Conceição Evaristo, é uma obra delicada e poética que explora o vínculo entre pais e filhos. O livro narra a história de um menino que, à medida que cresce, começa a questionar o mundo ao seu redor e os laços afetivos que moldam sua vida. Com sensibilidade, a narrativa aborda temas como amor, cuidado, amadurecimento e a importância das relações familiares. É um livro que toca profundamente o leitor, trazendo reflexões sobre o que realmente importa na jornada da vida.", AnaliseCritica = "Publicado em 2018, Canção para ninar menino grande é o quinto romance de Conceição Evaristo e foi recentemente incluído na lista de leituras obrigatórias da Fuvest. A obra é uma polifonia de vozes femininas que narram a trajetória de Fio Jasmim, um homem negro sedutor, cuja busca por validação masculina e racial o leva a relacionamentos marcados por infidelidade e superficialidade.A narrativa é conduzida por uma narradora anônima, que nunca conheceu Fio pessoalmente, mas reuniu histórias contadas por mulheres que se envolveram com ele. Essa estrutura reforça o conceito de escrevivência, criado por Evaristo, que mistura vivência e escrita como forma de resistência e expressão.Fio Jasmim é casado com Pérola Maria desde os vinte anos, mas mantém diversas amantes ao longo da vida. Seu comportamento é influenciado por um trauma de infância: o desejo frustrado de ser o “Príncipe Negro” em uma peça escolar, papel que foi dado a um colega branco. Esse episódio marca sua obsessão por conquistar mulheres como forma de compensação e afirmação.As mulheres da narrativa — Juventina, Neide, Aurora, Antonieta, Dolores, Dalva, Angelina e Eleonora — não são apenas vítimas ou coadjuvantes. Elas têm histórias próprias, desejos, dores e projetos, representando diferentes dimensões da identidade feminina, da classe social e da raça. A sororidade entre elas cria uma rede afetiva que se contrapõe à solidão e ao abandono.", Contexto = "Moçambique contemporâneo, com heranças da guerra civil e da cultura oral africana. Explora os traumas da guerra, a delicadeza das relações humanas e a reconstrução da identidade em meio à dor. Com linguagem poética e metafórica, Mia resgata a alma africana em conflito com a modernidade.", ArquivoFoto = "/img/cancao para ninar menino grande.jpg"},
            new Livro { Id = 10, FaculdadeId = 2, Nome = "Canções escolhidas do Cartola", PDF = "/PFD/Cartola.pdf", Resumo = "Canções Escolhidas, de Cartola, é uma seleção de músicas que reflete a profundidade lírica e a crítica social do mestre do samba. As canções abordam temas como amor, saudade, tempo e a vida no morro, destacando a riqueza da cultura brasileira e a oralidade poética. Essa obra foi incluída como leitura obrigatória no vestibular da 1, valorizando a conexão entre música e literatura.", AnaliseCritica = "Lorem Ipsum", Contexto = "Cartola viveu em um período de intensas mudanças no Brasil, especialmente no Rio de Janeiro, com o crescimento das favelas e a urbanização. Suas músicas são marcadas por sua vivência na Mangueira, um dos berços do samba carioca. Ele capturou as alegrias e dores do povo, compondo letras que ressoam com a experiência humana universal.", ArquivoFoto = "/img/CARTOLA.jpg"},
            new Livro { Id = 11, FaculdadeId = 1, Nome = "Conselhos à minha filha", PDF = "", Resumo = "Conselhos à Minha Filha de Nísia Floresta é uma obra escrita no formato de cartas, onde a autora compartilha reflexões, lições e orientações sobre a vida com sua filha. O livro oferece conselhos sobre temas como amor, casamento, virtudes, educação, relações sociais e a importância da moralidade, tudo imerso em um contexto histórico do século XIX. Com uma linguagem sensível e perspicaz, Girardin apresenta suas ideias de forma pragmática, mas também carinhosa, buscando preparar sua filha para enfrentar os desafios do mundo. A obra reflete os valores e costumes da época, mas também possui insights que permanecem atemporais, tornando-se uma leitura que dialoga com gerações além do seu tempo.", AnaliseCritica = "Publicado em 1842, Conselhos à minha filha é o primeiro trabalho autoral de Nísia Floresta e marca um momento pioneiro na literatura brasileira: trata-se da primeira obra escrita por uma mulher publicada no país. Estruturado como uma carta endereçada à filha Lívia, o livro reúne conselhos sobre virtudes, educação e conduta moral, refletindo o contexto social do Brasil oitocentista, em que os direitos das mulheres eram amplamente negados.No prefácio, Nísia escreve à filha no dia de seu aniversário de 12 anos, afirmando que prefere oferecer conselhos em vez de presentes materiais. Ela exalta o amor materno como o mais poderoso dos sentimentos e afirma que o título de mãe é o que verdadeiramente enobrece a mulher. A autora compartilha sua própria trajetória de superação, destacando que, mesmo sendo uma mulher sem apoio e recursos, encontrou forças para estudar e educar a filha.A obra defende com veemência a educação feminina, tema recorrente na produção de Nísia Floresta. Ela incentiva Lívia a estudar e cultivar virtudes morais e cristãs, como a obediência, a bondade e a generosidade. A obediência, segundo Nísia, deve ser praticada com prazer e consciência, não por medo. O bem deve ser feito por sua própria natureza, e o mal evitado por suas consequências espirituais.A autora também critica a vaidade, que considera um vício atribuído injustamente às mulheres, e exalta a modéstia, a simplicidade e a naturalidade. Ela aconselha a filha a se dedicar aos oprimidos, cultivar a caridade e rejeitar a ostentação. A felicidade dos outros deve ser prioridade, pois isso revela a verdadeira virtude.Ao tratar do contato com o sexo oposto, Nísia alerta sobre dois tipos de homens: o comum, que valoriza apenas a aparência feminina, e o raro, que admira os talentos e virtudes das mulheres. É este último que ela recomenda à filha, pois ele não teme que a mulher ultrapasse os limites impostos socialmente.No final da obra, Nísia resume seus conselhos em versos poéticos, reforçando a importância da sabedoria, da generosidade e do aprimoramento espiritual. Conselhos à minha filha é, portanto, uma obra que combina afeto, crítica social e defesa da educação como ferramenta de emancipação feminina. Sua inclusão na lista de leituras obrigatórias da Fuvest para os vestibulares de 2028 e 2029 reafirma sua relevância histórica e literária.", Contexto = "Portugal, final do século XIX, período marcado pela ascensão dos ideais republicanos, o início da luta pelos direitos das mulheres e uma sociedade ainda bastante conservadora. Considerada uma das vozes do protofeminismo lusófono. Ainda que de forma moderada, propunha uma autonomia intelectual e moral para a mulher.", ArquivoFoto = "/img/conselhos a minha filha.jpg"},
            new Livro { Id = 12, FaculdadeId = 2, Nome = "Dom Casmurro", PDF = "", Resumo = "Dom Casmurro, escrito por Machado de Assis, é um dos maiores clássicos da literatura brasileira. O livro é narrado em primeira pessoa por Bento Santiago, conhecido como Dom Casmurro, que revisita sua vida com um olhar melancólico e reflexivo. Ele conta sua história de amor com Capitu, uma mulher enigmática e de olhos de ressaca, e narra os desafios e tragédias que marcaram suas vidas. O ponto central da obra é a dúvida que atormenta Dom Casmurro: será que Capitu foi infiel e teve um filho com seu melhor amigo, Escobar? Essa questão nunca é respondida de forma definitiva, tornando o livro um estudo fascinante sobre ciúmes, memória e subjetividade. Machado de Assis utiliza ironia, ambiguidade e uma narrativa sofisticada para criar uma obra aberta à interpretação, que desafia o leitor a formar suas próprias conclusões.", AnaliseCritica = "Lorem Ipsum", Contexto = "Final do século XIX, transição entre Império e República, escravidão recém-abolida, forte influência da elite patriarcal carioca. Machado critica sutilmente a sociedade patriarcal e a hipocrisia da elite. Obra-prima do Realismo brasileiro, famosa pelo narrador Bentinho, não-confiável, mostrando que ele é um retrato do homem burguês inseguro, machista e controlador.", ArquivoFoto = "/img/d casmurro.jpg"},
            new Livro { Id = 13, FaculdadeId = 1, Nome = "Geografia", PDF = "", Resumo = "Geografia, publicado em 1967, é uma coletânea de poesia na qual Sophia de Mello Breyner Andresen estabelece uma íntima relação entre o espaço físico e o universo interior da memória e dos sentimentos. A obra utiliza uma linguagem lírica, marcada pela concisão e musicalidade, para transformar cada paisagem, cada ambiente e cada instante numa experiência simbólica que vai muito além do aspecto literal da geografia. Com seus versos precisos, a autora convida o leitor a perceber que os lugares carregam significados que se entrelaçam à identidade pessoal e coletiva.", AnaliseCritica = "Publicado em 1842, Conselhos à minha filha é o primeiro trabalho autoral de Nísia Floresta e marca um momento pioneiro na literatura brasileira: trata-se da primeira obra escrita por uma mulher publicada no país. Estruturado como uma carta endereçada à filha Lívia, o livro reúne conselhos sobre virtudes, educação e conduta moral, refletindo o contexto social do Brasil oitocentista, em que os direitos das mulheres eram amplamente negados.No prefácio, Nísia escreve à filha no dia de seu aniversário de 12 anos, afirmando que prefere oferecer conselhos em vez de presentes materiais. Ela exalta o amor materno como o mais poderoso dos sentimentos e afirma que o título de mãe é o que verdadeiramente enobrece a mulher. A autora compartilha sua própria trajetória de superação, destacando que, mesmo sendo uma mulher sem apoio e recursos, encontrou forças para estudar e educar a filha.A obra defende com veemência a educação feminina, tema recorrente na produção de Nísia Floresta. Ela incentiva Lívia a estudar e cultivar virtudes morais e cristãs, como a obediência, a bondade e a generosidade. A obediência, segundo Nísia, deve ser praticada com prazer e consciência, não por medo. O bem deve ser feito por sua própria natureza, e o mal evitado por suas consequências espirituais.A autora também critica a vaidade, que considera um vício atribuído injustamente às mulheres, e exalta a modéstia, a simplicidade e a naturalidade. Ela aconselha a filha a se dedicar aos oprimidos, cultivar a caridade e rejeitar a ostentação. A felicidade dos outros deve ser prioridade, pois isso revela a verdadeira virtude.Ao tratar do contato com o sexo oposto, Nísia alerta sobre dois tipos de homens: o comum, que valoriza apenas a aparência feminina, e o raro, que admira os talentos e virtudes das mulheres. É este último que ela recomenda à filha, pois ele não teme que a mulher ultrapasse os limites impostos socialmente.No final da obra, Nísia resume seus conselhos em versos poéticos, reforçando a importância da sabedoria, da generosidade e do aprimoramento espiritual. Conselhos à minha filha é, portanto, uma obra que combina afeto, crítica social e defesa da educação como ferramenta de emancipação feminina. Sua inclusão na lista de leituras obrigatórias da Fuvest para os vestibulares de 2028 e 2029 reafirma sua relevância histórica e literária.", Contexto = "Obra contemporânea, publicada por um dos grandes nomes da literatura lusófona atual. Valter Hugo Mãe é conhecido por seu estilo poético e pelas narrativas que tocam temas humanos em profundidade.  A literatura de Valter Hugo Mãe representa uma fase da literatura pós-moderna que mistura lirismo, crítica social e um profundo humanismo.", ArquivoFoto = "/img/geografia.jpg"},
            new Livro { Id = 14, FaculdadeId = 2, Nome = "Incidente em Antares", PDF = "", Resumo = "Incidente em Antares, de Érico Veríssimo, é um romance que mistura realismo mágico e crítica social. O livro é dividido em duas partes: a primeira, Antares, apresenta a cidade fictícia de Antares, no sul do Brasil, e explora os conflitos políticos, econômicos e sociais que permeiam a vida dos moradores. A cidade é dominada por oligarquias e marcada por desigualdades e corrupção.A segunda parte, O Incidente, traz o elemento fantástico: durante uma greve geral, sete mortos, cujos funerais não podem ser realizados por causa da paralisação, levantam de seus caixões e passam a circular pela cidade. Esses mortos começam a revelar segredos e hipocrisias dos vivos, expondo as falhas da sociedade de Antares. A obra utiliza esse evento sobrenatural para promover reflexões sobre moralidade, política e as contradições humanas.", AnaliseCritica = "Lorem Ipsum", Contexto = "Ditadura militar no Brasil (1964/1985), censura, repressão, mas também surgimento de vozes contestadoras na literatura. Sátira política poderosa, os mortos que se recusam a ser enterrados simbolizam ir contra um regime que não quer encarar seus próprios crimes. Usa o realismo fantástico para criticar os abusos de poder e a degeneração moral dos governantes.", ArquivoFoto = "/img/incidente em antares.jpg"},
            new Livro { Id = 15, FaculdadeId = 2, Nome = "João Miguel", PDF = "", Resumo = "João Miguel, escrito por Rachel de Queiroz, é um romance que explora temas como crime, arrependimento e a condição humana. A história acompanha João Miguel, um homem simples que, em um momento de impulsividade, comete um homicídio durante uma briga de bar. Ele é preso e a maior parte do livro se passa na cadeia, onde o protagonista reflete sobre sua vida, suas ações e o impacto de suas escolhas. A obra destaca a psicologia do preso, a solidão e a desumanização do sistema carcerário, enquanto também aborda questões sociais e a injustiça. Rachel de Queiroz utiliza uma narrativa rica em detalhes e camadas, proporcionando ao leitor uma experiência profunda e reflexiva.", AnaliseCritica = "Lorem Ipsum", Contexto = "Publicado em 1932, em um Brasil recém-saído da Revolução de 1930, com fortes transformações sociais e políticas. Rachel, com sua sensibilidade realista, denuncia as mazelas da pobreza e da negligência social. O livro faz parte da primeira geração modernista, com forte crítica social.", ArquivoFoto = "/img/joao miguel.jpg"},
            new Livro { Id = 16, FaculdadeId = 1, Nome = "Memórias de Marta", PDF = "", Resumo = "Memórias de Marta, de Júlia Lopes de Almeida, é uma obra que narra a história de uma mulher chamada Marta, que revisita suas memórias enquanto tenta compreender sua jornada de vida. O livro explora temas como amor, sofrimento, perdas e a busca por sentido em meio aos desafios pessoais. Marta é um retrato de força e vulnerabilidade, e suas memórias são carregadas de emoção e reflexão sobre os vínculos familiares e os dilemas que enfrentou. Com uma escrita envolvente, a autora nos guia pelas lembranças de Marta, oferecendo ao leitor uma perspectiva íntima e humana sobre os altos e baixos da existência. É uma leitura que inspira e emociona.", AnaliseCritica = "Publicado originalmente como folhetim em 1888 e editado em livro em 1899, Memórias de Martha é o primeiro romance de Júlia Lopes de Almeida e integra a lista de leituras obrigatórias da Fuvest 2026. Narrado em primeira pessoa por uma mulher, o romance se passa em um cortiço do Rio de Janeiro no final do século XIX, abordando temas como desigualdade social, preconceito, transformações urbanas e, sobretudo, a situação da mulher e sua busca por emancipação por meio da educação e do trabalho. A protagonista, Martha, vive com a mãe em condições precárias após a morte do pai, que leva a família à pobreza e à perda do status social. A mãe torna-se lavadeira, enquanto Martha, ainda criança, enfrenta dificuldades para estudar e sobreviver em um ambiente insalubre. Apesar dos conselhos maternos para aprender tarefas domésticas, Martha vislumbra a ascensão social por meio da carreira de professora. Ela passa nos exames e conquista a liberdade financeira para ambas, mas acaba se casando por imposição da mãe, sem amor ou desejo de constituir família. Após o casamento, a morte da mãe mergulha Martha em profunda dor e desilusão, encerrando suas memórias permeadas por lutas e perdas.A obra apresenta uma estética realista, nascida como romance-folhetim, e retrata personagens marginalizados — mulheres empobrecidas, imigrantes miseráveis e ex-escravizados — promovendo uma crítica social contundente. Ao contrário do determinismo naturalista, Martha e sua mãe não se deixam moldar pelo meio, resistindo às condições degradantes do cortiço. A linguagem direta e envolvente da autora permite ao leitor acompanhar o amadurecimento da personagem sob sua própria perspectiva, revelando nuances de classe, gênero e raça.Memórias de Martha também oferece uma análise política e sociológica do Brasil pós-abolição, mostrando como mulheres pobres enfrentavam epidemias, fome e exclusão em uma sociedade patriarcal. A obra revela preconceitos raciais e de classe, como no julgamento da colega Mathilde, descrita como “feia, escura, marcada de bexigas”. Embora breve, o romance é denso e exige leitura atenta, com múltiplas camadas que exploram desde as mudanças urbanas do Rio até as tensões sociais do período.Comparado ao clássico O Cortiço, de Aluísio Azevedo, publicado dois anos depois, o romance de Júlia Lopes se diferencia por apresentar uma protagonista que ascende socialmente por meio da educação, enquanto Azevedo retrata a degradação moral dos moradores. A exclusão da obra de Júlia do cânone literário por décadas reflete o apagamento de autoras mulheres, que a nova lista da Fuvest busca reparar.Júlia Lopes de Almeida (1862-1934), autora erudita e prolífica, participou das reuniões fundadoras da Academia Brasileira de Letras, mas foi excluída por ser mulher. Sua trajetória literária inclui romances, contos, crônicas, teatro e literatura infantil, e sua atuação intelectual foi marcada pelo engajamento político e cultural. Memórias de Martha é uma obra essencial para compreender o Brasil do entresséculos sob o olhar feminino, revelando com sensibilidade e crítica as dores e resistências de uma mulher que ousou sonhar com liberdade.", Contexto = "Brasil do final do século XIX, em plena transição entre o Império e a República, com forte influência do patriarcado. A personagem Marta é uma mulher que luta contra as amarras da sociedade patriarcal. Um marco da literatura feminista no Brasil, embora tenha sido ignorado por muito tempo.", ArquivoFoto = "/img/memorias de marta.jpg"},
            new Livro { Id = 17, FaculdadeId = 2, Nome = "Morangos Mofados", PDF = "", Resumo = "Morangos Mofados, de Caio Fernando Abreu, é uma coletânea de contos publicada em 1982, que retrata os desafios e angústias vividos na época da ditadura militar no Brasil. As histórias abordam temas como solidão, medo, amor, repressão e busca por liberdade, conectando os sentimentos dos personagens ao contexto político e social.", AnaliseCritica = "Lorem Ipsum", Contexto = " Anos 1980, Brasil sob ditadura militar e início da epidemia de HIV/AIDS.  Um dos marcos da literatura LGBTQIA+ no Brasil. Caio explora com sensibilidade e angústia a marginalidade e o afeto em tempos sombrios.", ArquivoFoto = "/img/morangos mofados.jpg"},
            new Livro { Id = 18, FaculdadeId = 1, Nome = "Nebulosas", PDF = "", Resumo = "Nebulosas, de Narcisa Amália, é uma coletânea de poemas publicada em 1872, que reflete o lirismo romântico e a profundidade emocional da autora. A obra está dividida em três partes, contendo um total de 44 poemas. Os temas abordados incluem questões sociais, como a abolição da escravatura, além de reflexões intimistas e femininas, frequentemente relacionadas à natureza. A linguagem rica e poética de Narcisa Amália destaca-se pelo uso de termos raros e pela atmosfera contemplativa e mística que permeia os versos. A obra também traz uma perspectiva única, considerando o contexto histórico em que foi escrita, quando as mulheres tinham pouca presença no mundo literário erudito.", AnaliseCritica = "Publicado em 1872, Nebulosas é o único livro da poeta, tradutora, crítica literária e primeira jornalista profissional brasileira Narcisa Amália de Campos. A obra reúne 44 poemas divididos em três partes e foi redescoberta no século XXI, sendo incluída na lista de leituras obrigatórias da Fuvest 2026. Com forte influência do Romantismo, o livro combina lirismo, crítica social e engajamento político, abordando temas como abolição da escravatura, republicanismo, natureza e identidade feminina.A primeira parte da obra é composta por um único poema, também intitulado Nebulosas, que estabelece o tom lírico da autora — melancólico, introspectivo e marcado pela imagem da noite. A segunda parte, com 27 poemas, é considerada o momento mais romântico da autora, com exaltações à natureza, à infância e ao amor em suas diversas formas, inclusive o amor entre mulheres, mães e amigas. Já a terceira parte, com 16 poemas, é a mais abolicionista e republicana, homenageando figuras importantes da vida da autora e denunciando injustiças sociais, especialmente a escravidão.Narcisa Amália foi uma mulher à frente de seu tempo. Nascida em São João da Barra, no Rio de Janeiro, recebeu uma educação incomum para mulheres da época, aprendendo francês e se profissionalizando como jornalista. Atuou como redatora, tradutora e revisora em diversos jornais, e sua obra foi elogiada por nomes como Machado de Assis e reconhecida pelo imperador D. Pedro II, que a premiou com a Lira de Ouro. Apesar do sucesso inicial, Narcisa enfrentou difamações, dois divórcios — raros no século XIX — e acabou esquecida pela crítica literária, especialmente após a ascensão do Modernismo.A poesia de Nebulosas é marcada por influências de autores como Gonçalves Dias, Castro Alves e Victor Hugo, e se insere na terceira geração do Romantismo brasileiro, conhecida como condoreira, que utiliza a literatura como instrumento de denúncia social. Narcisa escreve sobre a escravidão, os direitos das mulheres e os marginalizados, sendo chamada de “poeta dos livres”. No entanto, sua atuação se deu principalmente no campo literário e jornalístico, e não na militância direta.Poemas como O Africano e o Poeta, Invocação, O Itatiaia e A Resende são considerados estratégicos para a leitura da obra, revelando a complexidade de sua lírica e o protagonismo feminino. A figura da “peregrina”, presente em seus versos, simboliza a mulher que caminha, conhece e escreve — uma representação poderosa para o século XIX.Nebulosas é uma obra essencial para compreender o papel das mulheres na formação literária e intelectual do Brasil. Sua linguagem sofisticada exige leitura atenta, e sua inclusão na Fuvest representa um resgate histórico de uma autora pioneira, cuja voz ainda ecoa com força e relevância.", Contexto = "Brasil no final do século XIX, momento de urbanização e de ascensão da mulher na literatura. Considerada precursora do feminismo literário brasileiro, teve participação ativa no debate público da época, inclusive na fundação da Academia Brasileira de Letras (onde não foi aceita por ser mulher).", ArquivoFoto = "/img/nebulosas.jpg"},
            new Livro { Id = 19, FaculdadeId = 2, Nome = "No seu pescoço", PDF = "", Resumo = "No Seu Pescoço (*The Thing Around Your Neck*), da escritora Chimamanda Ngozi Adichie, é uma coletânea de 12 contos publicada em 2009. A obra aborda questões relacionadas a identidade, imigração, desigualdade social, e os desafios enfrentados por mulheres, especialmente as de origem africana, no mundo contemporâneo.", AnaliseCritica = "Lorem Ipsum", Contexto = "Nigéria e diáspora africana no século XXI. Contos fortes e feministas, que denunciam as desigualdades de gênero, raça e classe, tanto na África quanto no Ocidente.", ArquivoFoto = "/img/no seu pescoço.jpg"},
            new Livro { Id = 20, FaculdadeId = 2, Nome = "Nós matamos o cão tinhoso", PDF = "", Resumo = "Nós Matamos o Cão Tinhoso, de Luís Bernardo Honwana, é uma coleção de contos que explora as complexidades da vida em Moçambique durante o período colonial. O conto que dá título ao livro narra a história de um grupo de meninos que decide matar um cão doente e perigoso, o Cão Tinhoso. A narrativa, simples na superfície, é carregada de simbolismo, revelando temas como violência, opressão, responsabilidade e a perda da inocência. Honwana utiliza uma linguagem rica e sensível para capturar as dinâmicas sociais e culturais de Moçambique, oferecendo ao leitor uma reflexão profunda sobre as relações humanas e os impactos do colonialismo. Cada conto da coletânea contribui para a construção de um panorama emocional e crítico do contexto moçambicano.", AnaliseCritica = "Lorem Ipsum", Contexto = "Moçambique ainda sob domínio colonial português. Publicado pouco antes do início da guerra de independência (1964/1974). Marco da literatura moçambicana. A história que dá nome ao livro mostra uma criança negra obrigada a matar um cão doente — metáfora do racismo e da opressão colonial.", ArquivoFoto = "/img/nos matamos o cao tinhoso.jpg"},
            new Livro { Id = 21, FaculdadeId = 1, Nome = "O Cristo Cigano", PDF = "", Resumo = "O Cristo Cigano, de Sophia de Mello Breyner Andresen, é uma coletânea de 11 poemas que exploram temas como arte, violência, espiritualidade e contrastes entre o sagrado e o profano. A obra narra a saga de um escultor em busca da imagem perfeita para representar Jesus Cristo morto. Durante sua jornada, ele encontra inspiração em um cigano, o que leva a reflexões profundas sobre a criação artística e os dilemas da existência", AnaliseCritica = "Publicado em 1961, O Cristo Cigano é uma obra singular da poeta portuguesa Sophia de Mello Breyner Andresen, recentemente incluída na lista de leituras obrigatórias da Fuvest 2026. Com apenas 12 poemas, o livro exige sensibilidade e atenção para captar os contrastes entre vida e morte, sagrado e profano, arte e violência. A obra foi inspirada em uma conversa entre Sophia e o poeta brasileiro João Cabral de Melo Neto, com quem ela compartilhava admiração mútua e afinidades poéticas.Durante um passeio por Sevilha, onde Cabral atuava como cônsul, ele contou a Sophia a lenda de Francisco Antonio Ruiz Gijón, escultor espanhol do século XVII que teria assassinado um cigano — apelidado de El Cachorro — para capturar seu semblante agonizante e esculpir a imagem de Cristo agonizante. Outra versão da lenda diz que Gijón apenas presenciou o assassinato e desenhou o rosto da vítima com carvão. A escultura real, chamada Santíssimo Cristo de la Expiración, datada de 1682, pode ser vista na Catedral do Cristo Cachorro, em Sevilha.Essa história impressionou Sophia e deu origem ao livro, cujo poema de abertura, “A palavra faca”, faz referência explícita a João Cabral. Os versos exploram a obsessão pela forma artística e pela perfeição, os limites dessa obsessão, e os sacrifícios feitos em nome de um ideal. A obra convida à reflexão sobre a desumanização do modelo artístico, o arbítrio e o destino, e os extremos que se atingem em nome da estética — tanto da arte quanto do corpo humano.Embora os poemas possam ser lidos isoladamente, a leitura integral revela uma narrativa com cronologia e relações de causa e consequência. Sophia utiliza recursos estilísticos como antíteses, anáforas e paralelismos, aproximando-se das tradições ibéricas de romances populares em verso. A atmosfera e os sentimentos do escultor mudam ao longo dos poemas, e essas variações são fundamentais para compreender a profundidade da obra.Sophia de Mello Breyner Andresen nasceu em Porto, em 1919, e pertence à mesma geração de escritores brasileiros como Cecília Meireles, João Cabral de Melo Neto e Clarice Lispector. Sua poesia é crítica aos totalitarismos, sensível às tensões da Segunda Guerra Mundial e da ditadura salazarista. Ela foi uma poeta do social e do natural, do concreto e do abstrato, do moderno e das referências clássicas. Sua formação literária abrange desde a Antiguidade até os grandes nomes da poesia portuguesa e brasileira.O Cristo Cigano é, portanto, uma obra breve, mas intensa, que entrelaça história, mito, arte e crítica social. Sua leitura exige atenção aos detalhes formais e simbólicos, e oferece aos vestibulandos uma oportunidade de explorar os limites da criação artística e os dilemas éticos que a acompanham.", Contexto = "Brasil do século XX, em processo de urbanização e enfrentando mudanças sociais intensas. Traz uma crítica à intolerância e ao julgamento social, ao mesmo tempo em que promove uma releitura da figura de Cristo como símbolo de empatia e acolhimento.", ArquivoFoto = "/img/o cristo cigano.jpg"},
            new Livro { Id = 22, FaculdadeId = 2, Nome = "Olhos d'água", PDF = "", Resumo = "Olhos D'Água, de Conceição Evaristo, é uma coletânea de contos publicada em 2014 que explora as múltiplas facetas da experiência humana, especialmente sob a perspectiva de mulheres negras e marginalizadas. Com uma escrita poética e sensível, Evaristo aborda temas como pobreza, racismo, violência, e afetos, revelando a força e a resiliência dessas vidas invisibilizadas. Cada conto é carregado de emoção e profundidade, destacando a busca por identidade, justiça e dignidade. A metáfora dos olhos d'água sugere tanto o choro de dor quanto a capacidade de transformação e resistência.", AnaliseCritica = "Lorem Ipsum", Contexto = "Brasil contemporâneo, marcado pelo racismo estrutural, pobreza e desigualdade. Conceição dá voz às histórias silenciadas de mulheres negras, misturando lirismo e denúncia social com o conceito de escrevivência.", ArquivoFoto = "/img/olhos d agua.jpg"},
            new Livro { Id = 23, FaculdadeId = 1, Nome = "Opúsculo Humanitário", PDF = "", Resumo = "Opúsculo Humanitário, de Nísia Floresta, é uma obra que reflete as ideias da autora sobre a abolição da escravatura e os valores humanitários. Publicado em 1869, o livro apresenta argumentos contra a escravidão e defende a liberdade como um direito fundamental. Nísia discorre sobre os impactos sociais e morais da escravidão, propondo uma sociedade mais justa e igualitária. O texto é considerado um marco no pensamento abolicionista no Brasil, reunindo tanto reflexões filosóficas quanto críticas sociais. É um convite para entender a luta pela dignidade humana em um momento histórico crucial.", AnaliseCritica = "Publicado em 1853, Opúsculo Humanitário é uma obra fundamental para compreender o pensamento social e feminista no Brasil do século XIX. Escrito por Nísia Floresta, pioneira do feminismo brasileiro, o texto apresenta-se em formato ensaístico e não ficcional, articulando reflexões sobre a condição das mulheres, a desigualdade social, a escravidão e a situação dos povos indígenas. Sua linguagem é marcada pela eloquência e por recursos retóricos como metáforas, anáforas e paradoxos, que reforçam o tom persuasivo e combativo.Nísia Floresta, escritora, poetisa e educadora, rompeu com os padrões de sua época ao defender publicamente a educação feminina como instrumento de emancipação. Fundadora de um dos primeiros colégios para meninas no Brasil, ela se inspirou em ideias iluministas e nas reflexões da inglesa Mary Wollstonecraft, mas adaptou-as à realidade brasileira, marcada por um regime monárquico, patriarcal e escravocrata. Sua atuação intelectual incluiu também a defesa da causa indígena e a denúncia da escravidão, o que a torna uma figura singular na história cultural do país.No Opúsculo Humanitário, a autora afirma que sem educação plena não há igualdade de gênero. Para ela, a inferioridade atribuída às mulheres não é natural, mas fruto da exclusão educacional e das circunstâncias sociais. Critica duramente o ensino limitado às tarefas domésticas, a ausência de preparo intelectual e a má qualidade das instituições de ensino, conduzidas muitas vezes por diretores e professores despreparados. Embora compartilhe da visão dominante no século XIX de que mulheres instruídas formariam melhores esposas e mães, sua perspectiva ultrapassa esse objetivo ao defender que o saber é direito e necessidade para o exercício da cidadania.Outro mérito da obra é ampliar a pauta para além da questão de gênero, incluindo a situação de mulheres escravas e indígenas, denunciando a violência, a exploração e a desumanização a que eram submetidas. Floresta reconhece nessas populações a dupla opressão — por gênero e por raça — e questiona a moralidade de uma sociedade que se dizia civilizada mas mantinha práticas brutais. Seu posicionamento contra a escravidão e em favor do respeito às culturas originárias revela um pensamento social progressista e interseccional para o seu tempo.A religiosidade também aparece como tema, vista por Nísia como um elemento de consolo e orientação moral, mas sem abrir mão da defesa de uma educação laica, que privilegie o desenvolvimento intelectual autônomo. Aqui, nota-se um equilíbrio entre a crítica às imposições dogmáticas e o reconhecimento do papel da fé na vida cotidiana.A força de sua escrita está na conjugação de argumentação racional e sensibilidade ética, fazendo uso de figuras de linguagem para reforçar a indignação contra injustiças — como na metáfora “a ignorância é um véu espesso que obscurece o espírito feminino” — e para inspirar ação. Ao final, Opúsculo Humanitário se consolida como um manifesto em favor da justiça social e da igualdade, no qual a educação é apresentada como alicerce indispensável para transformar a condição feminina e democratizar o acesso à cidadania.O impacto de Nísia Floresta está em ter se insurgido contra estruturas de poder em um contexto extremamente hostil ao pensamento emancipador. Sua obra permanece atual, pois as questões que levanta — desigualdade de gênero, acesso à educação, racismo estrutural e respeito às diferenças culturais — continuam a desafiar a sociedade brasileira. Opúsculo Humanitário não é apenas um marco do feminismo no Brasil, mas também um testemunho da resistência intelectual contra as opressões do século XIX, cuja relevância se mantém viva no debate contemporâneo sobre direitos humanos e educação.", Contexto = "Brasil do século XIX, sob o regime escravocrata e patriarcal. Nísia foi uma das primeiras vozes feministas e abolicionistas do Brasil. Primeira mulher a publicar um texto em defesa da mulher no Brasil. Seu pensamento antecipa muitos debates que só viriam a ganhar força no século XX. Neste opúsculo, ela defende os direitos das mulheres e dos indígenas, denunciando a opressão social.", ArquivoFoto = "/img/opusculo humanitario.jpg"},
            new Livro { Id = 24, FaculdadeId = 2, Nome = "Prosas seguidas de Odes Mínimas", PDF = "", Resumo = "Prosas seguidas de odes mínimas, de José Paulo Paes, é uma obra que reflete a singularidade da poesia do autor, marcada por uma linguagem simples e profundamente filosófica. Publicado em 1996, o livro traz um misto de prosas poéticas e breves poemas (as odes mínimas) que celebram o banal, o cotidiano e a essência das pequenas coisas.", AnaliseCritica = "Lorem Ipsum", Contexto = "Brasil do século XX, com influência do Modernismo e da poesia concreta. José Paulo une simplicidade e inteligência em textos que exploram o banal e o profundo com humor e poesia.", ArquivoFoto = "/img/prosas seguidas de odes minimas.jpg"},
            new Livro { Id = 25, FaculdadeId = 1, Nome = "Vida e Morte de M.J Gonzaga de Sá", PDF = "/PDF/Vida e Morte de M.J. Gonzaga de Sá", Resumo = "O livro Vida e Morte de M.J. Gonzaga de Sá, de Lima Barreto, é uma obra publicada em 1919 que aborda questões sociais, políticas e culturais do Brasil no início do século XX. A narrativa é conduzida por Augusto Machado, um jovem funcionário público, que relata a vida de seu amigo Gonzaga de Sá, um homem mais velho, também funcionário público, que reflete sobre as injustiças e contradições da sociedade brasileira.A obra critica a burocracia, o racismo e a desigualdade social, além de explorar temas como a solidão, o pessimismo e a busca por sentido em um mundo marcado por transformações urbanas e políticas. Gonzaga de Sá é retratado como um intelectual desencantado com a humanidade, enquanto Augusto tenta preservar a memória do amigo e compreender suas reflexões.", AnaliseCritica = "Publicado em 1919, Vida e Morte de M.J. Gonzaga de Sá, de Lima Barreto, é uma das obras mais representativas do Pré-Modernismo brasileiro. O romance rompe com a estrutura tradicional ao não apresentar um enredo linear com início, meio e fim definidos, utilizando-se da ficção como pano de fundo para reflexões filosóficas, críticas sociais e observações sobre o Rio de Janeiro no início do século XX. Narrado em primeira pessoa por Augusto Machado, um jovem mestiço e funcionário público, o livro relata a vida e os pensamentos de seu amigo mais velho, Manuel Joaquim Gonzaga de Sá, também funcionário público, culto e melancólico. Desde as primeiras páginas, sabemos que Gonzaga está morto, o que desloca o foco narrativo para a análise de suas ideias, experiências e da sociedade ao redor. Lima Barreto, filho de um tipógrafo negro, enfrentou dificuldades econômicas, preconceito racial e problemas de saúde mental ao longo da vida. Seu nome literário homenageia o pai, e sua obra se caracteriza por linguagem coloquial, crítica às elites e foco em personagens marginalizados. Reconhecido como precursor do Modernismo, antecipa debates estéticos e sociais que explodiriam na Semana de 1922, mas mantém vínculos com o Realismo e o Naturalismo. Sua escrita parte de um lugar de experiência direta com a exclusão social, conferindo autenticidade e contundência à crítica. No romance, o pano de fundo histórico é a Primeira República, marcada por transformações urbanas no Rio de Janeiro, política de “branqueamento” populacional, desigualdade social e contrastes entre o centro e os subúrbios. Nesse cenário, Lima Barreto constrói uma denúncia abrangente: o racismo estrutural impede a ascensão plena de Gonzaga, apesar de sua inteligência e erudição; a burocracia estatal é retratada como inútil e alienante, com funcionários apáticos preocupados apenas com formalidades vazias; a elite da Belle Époque é exposta como superficial, imitadora de costumes europeus e indiferente aos problemas sociais; e a mobilidade social, para negros e pobres, é praticamente impossível, evidenciando um determinismo social que condena talentos ao esquecimento. A crítica à “falsa erudição” também é central: Lima Barreto ridiculariza personagens que ostentam citações e títulos sem compreender o conteúdo, expondo o vazio intelectual de uma sociedade mais preocupada com aparência do que com pensamento genuíno. Outras frentes críticas incluem a corrupção política — com ataques diretos a figuras como o Barão do Rio Branco —, a obsessão pelo diploma como símbolo de ascensão social, a desigualdade urbana e a realidade dura dos subúrbios. Do ponto de vista literário, a obra recorre a ironia e sarcasmo para desmascarar comportamentos e instituições; a metáforas e antíteses para acentuar contrastes; e a hipérboles para intensificar emoções. Há também intertextualidade filosófica (Bacon, Nietzsche, Rousseau, Schopenhauer) e literária (Dostoiévski, Tolstói, George Eliot), enriquecendo as reflexões sobre moral, sociedade e condição humana. O desfecho, com a morte quase despercebida de Gonzaga de Sá, é simbólico: um intelectual crítico, ignorado em vida, morre sem reconhecimento, reforçando a denúncia contra uma sociedade que valoriza aparências e silencia vozes dissonantes. A solidão do protagonista ecoa a do próprio Lima Barreto, que também se sentiu estrangeiro em seu país.Mesmo ambientada há mais de um século, a obra permanece atual. Questões como racismo, desigualdade social, corrupção política, burocracia ineficiente e cultura de aparências continuam a marcar a realidade brasileira. Vida e Morte de M.J. Gonzaga de Sá é, assim, não apenas um retrato de época, mas também um espelho incômodo e ainda pertinente, que desafia o leitor a confrontar as permanências de um passado que insiste em não passar.", Contexto = "Início do século XX, República Velha, racismo, elitismo, burocracia. Lima Barreto critica duramente a hipocrisia da elite intelectual brasileira, usando ironia e melancolia para mostrar o vazio existencial de seu protagonista.", ArquivoFoto = "/img/vida e morte.jpg"},
        };
